function dateFormat(t){return 10>t?t="0"+t:t.toString()}angular.module("MLBScoresApp",[]).controller("MainCtrl",["$scope","games",function(t,n){n.success(function(n){t.games=n}),t.status=function(t){return"Final"===t.status.status?"Final":"Y"===t.status.top_inning?"Top ":"Bottom "+t.status.inning}}]).factory("games",["$http",function(t){var n=new Date,e=n.getDate(),a=n.getMonth()+1,o=n.getFullYear();return e=dateFormat(e),a=dateFormat(a),o=dateFormat(o),t.get("http://gd2.mlb.com/components/game/mlb/year_"+o+"/month_"+a+"/day_"+e+"/master_scoreboard.json").success(function(t){return t}).error(function(t){return t})}]);